	  neg_ans=1;
	  pos_ans=2;
	}
      }else if(M>0){
	if(!contNeg){
	  pos_ans=3;
	}else{
	  neg_ans=2;
	  pos_ans=1;
	}
      }else{
	if(extreme[0]>0&&extreme[1]>0){
	  pos_ans=2;
	}else if(extreme[0]<0&&extreme[1]<0){
	  neg_ans=2;
