#include <iostream>
#include <vector>
#include <cmath>
using namespace std;

vector<double> divide(double a,double b,double c,double d,double p){
	vector<double> ans;
	double v = a;
	ans.push_back(v);
	v = v*p + b;
	ans.push_back(v);
	v = v*p+c;
	ans.push_back(v);
	v = v*p+d;
	ans.push_back(v);
	return ans;
}
double solve(double a,double b,double c,double d){
	double l = -1000 , r = 1000;
	for(int i=0;i<82;i++){
		double x = (l+r)/2.0;
		if(a * x * x * x + b * x * x + c * x + d > 0){
			r = x;
		}else{
			l = x;
		}
	}
	return l;
}
bool isZero(double n){
	return abs(n) < 1e-10; 
}
pair<int,int> count(double a,double b,double c){
	pair<int,int> ans;
	double inside = b * b - 4 * a * c ;
	if( inside < -1e-10) return ans;
	if( isZero(inside) ) inside = 0;
	double mx = ( -b + sqrt(inside) ) / (2 * a) , mi = ( -b - sqrt(inside) ) / (2 * a);
	if(mx > 1e-10) ans.first++;
	else if( mx < -1e-10 )ans.second++;
	if(mi > 1e-10) ans.first++;
	else if( mi < -1e-10 )ans.second++;
	return ans;
}
int main(){
	int n;
	cin >> n;
	double a,b,c,d;
	while(cin >> a >> b >> c >> d){
		int mCount = 0 , pCount = 0;
		if(isZero(d)){
			pCount += count(a,b,c).first;
			mCount += count(a,b,c).second;
		}else{
			double x = solve(a,b,c,d);

			if(x > 1e-10) pCount++;
			else if( x < -1e-10 ) mCount++;
			vector<double> o = divide(a,b,c,d,x);
			pCount += count(o[0],o[1],o[2]).first;
			mCount += count(o[0],o[1],o[2]).second;
		}
		cout << pCount << " " << mCount << endl;
	}
}

