bool is_zero(double x){
    if(abs(x) < EPS) return true;
    return false;
}

void add(double x){
    if(is_zero(x)) return;
    if(x > 0) pl++;
    else if(x < 0) mn++;
    return;
}

void bisearch(double x, double y){
    if(func(y) < func(x)) swap(x,y);
    if(is_zero(func(x))){ add(x); return;}
    if(is_zero(func(y))){ add(y); return;}
    double mid;
    int ct = 0;
    rep(i,100){
        mid = (x+y)/2;
        if(is_zero(func(mid))) break;
        if(func(mid) > 0) y = mid;
        else if(func(mid) < 0) x = mid;
        ct++;
    }
    if(ct == 100) return;
    add(mid);
    return;
}

int main(){
    int N;
