        cin >> a >> b >> c >> d;
        b /= a, c /= a, d /= a, a = 1;
        pl = 0, mn = 0;
        double D = b*b - 3*a*c;
        double xl,xs;

        if(D < 0) bisearch(-10000,10000);
        else{
            xs = (-b-sqrt(D))/(3*a);
            xl = (-b+sqrt(D))/(3*a);
            bisearch(-10000,min(xs,xl));
            bisearch(min(xs,xl),max(xs,xl));
            bisearch(max(xs,xl),10000);
        }

        cout << pl << " " << mn << endl;
    }

}

