					if (d > 0)p = 0, m = 3;
					else if (d < 0)p = 1, m = 2;
					else p = 0, m = 2;
				}
				else if (k1 > 0) {
					if (d > 0)p = 2, m = 1;
					else if (d < 0)p = 3, m = 0;
					else p = 2, m = 0;
				}
				else {
					if (d > 0)p = 2, m = 1;
					else if (d < 0)p = 1, m = 2;
					else p = 1, m = 1;
				}
			}
		}
		else if (D < 0) {
			if (d > 0)p = 0, m = 1;
			else if (d < 0)p = 1, m = 0;
			else p = 0, m = 0;
		}
		else {
			if (d > 0) {
				if (y(a, b, c, d, -b / (2 * a)) == 0)p = 0, m = 3;
				else p = 0, m = 1;
			}
			else if (d < 0) {
				if (y(a, b, c, d, -b / (2 * a)) == 0)p = 3, m = 0;
				else p = 1, m = 0;
			}
			else p = 0, m = 0;
		}
		printf("%d %d\n", p, m);
	}

}

