const int MAX = 510;

int dx[] = {1, 0, -1, 0};
int dy[] = {0, 1, 0, -1};

char field[MAX][MAX];
bool found[26];
Point alpha[26];

int H, W;

bool canReach(char C, int cx, int cy, int x, int y){
  if(cx < x){
    for(int j = x ; j >= cx ; j--){
      if(field[y][j] != '.' && field[y][j] != C) return false;
    }
  }
  else if(cx > x){
    for(int j = x ; j <= cx ; j++){
      if(field[y][j] != '.' && field[y][j] != C) return false;
    }
  }

  if(cy < y){
    for(int i = y ; i >= cy ; i--){
      if(field[i][x] != '.' && field[i][x] != C) return false;
    }
  }
  else if(cy > y){
    for(int i = y ; i <= y ; i++){
      if(field[i][x] != '.' && field[i][x] != C) return false;
    }
  }
  return true;
}

bool canReach_x(char C, int x, int y1, int y2){
  if(y1 > y2) swap(y1, y2);
  for(int i = y1 ; i <= y2 ; i++){
    if(field[i][x] != '.' && field[i][x] != C) return false;
  }
  return true;
}

bool canReach_y(char C, int y, int x1, int x2){
  if(x1 > x2) swap(x1, x2);
  for(int j = x1 ; j <= x2 ; j++){
    if(field[y][j] != '.' && field[y][j] != C) return false;
  }
  return true;
}

int main(){
  while(cin >> H >> W){
    rep(i, H) cin >> field[i];

    memset(found, 0, sizeof(found));

    rep(i, H){
      rep(j, W){
	int c = field[i][j]-'A';
	if(!found[c]){
	  found[c] = true;
	  alpha[c].x1 = j, alpha[c].y1 = i;
	}
	else{
	  alpha[c].x2 = j, alpha[c].y2 = i;
	}	
      }
    }

    Point cross[26];
    rep(i, 26){
      if(found[i]){
	cross[i].x1 = alpha[i].x1, cross[i].y1 = alpha[i].y2;
	cross[i].x2 = alpha[i].x2, cross[i].y2 = alpha[i].y1;
      }
    }

    int ans = 0;

    rep(i, H){
      rep(j, W){
	char c = field[i][j];
	rep(k, 4){
	  int nx = j+dx[k], ny = i+dy[k];
	  if(nx < 0 || nx > W || ny < 0 || ny > H) continue;
	  if(field[i][j] == field[ny][nx]) found[field[i][j]-'A'] = false;
	}
      }
    }


    for(; ;){
      bool flag = false;
      rep(i, 26){
	if(found[i]){
	  int x1 = alpha[i].x1, y1 = alpha[i].y1, x2 = alpha[i].x2, y2 = alpha[i].y2;

	  if(x1 == x2){
	    if(canReach_x('A'+i, x1, y1, y2)){
	      field[y1][x1] = field[y2][x2] = '.';
	      found[i] = false;
	      ans += 2;
	      flag = true;
	    }
	  }
	  else if(y1 == y2){
	    if(canReach_y('A'+i, y1, x1, x2)){
	      field[y1][x1] = field[y2][x2] = '.';
	      found[i] = false;
	      ans += 2;
	      flag = true;
	    }
	  }	      	    	  	  
	  else if((canReach('A'+i, cross[i].x1, cross[i].y1, x1, y1) &&
		   canReach('A'+i, cross[i].x1, cross[i].y1, x2, y2)) ||
		  (canReach('A'+i, cross[i].x2, cross[i].y2, x1, y1) &&
		   canReach('A'+i, cross[i].x2, cross[i].y2, x2, y2))){
	    field[y1][x1] = field[y2][x2] = '.';
	    found[i] = false;
	    ans += 2;
	    flag = true;
	  }
	}
      }
      if(!flag) break;
    }

    cout << ans << endl;

  }

  return 0;
}

#include <bits/stdc++.h>
#define REP(i, n) for (int i = 0; i < (int)(n); i++)
using namespace std;

const int dx[4] = {1, 0, -1, 0};
const int dy[4] = {0, 1, 0, -1};

map<int, int> compress(vector<int>& unzip) {
  map<int, int> zip;
  for (auto x : unzip) REP(d, 3) zip[x + d - 1] = -1;
  int idx = 0;
  unzip = vector<int>();
  for (auto& p : zip) {
    unzip.push_back(p.first);
    p.second = idx;
    idx++;
  }
  return zip;
}

int main() {
  int m, n;
  cin >> m >> n;
  vector<string> c(m);
  REP(i, m) cin >> c[i];

////////////////////////////////////////
  vector<int> xs, ys;
  vector<char> cs;
  REP(i, m) REP(j, n) {
    if (c[i][j] == '.') continue;
    xs.push_back(i);
    ys.push_back(j);
    cs.push_back(c[i][j]);
  }
  vector<int> uzx(xs), uzy(ys);
  auto zx = compress(uzx);
  auto zy = compress(uzy);
  m = zx.size();
  n = zy.size();
  c = vector<string>(m, string(n, '.'));
  REP(k, cs.size()) {
    int x = xs[k];
    int y = ys[k];
    c[zx[x]][zy[y]] = cs[k];
  }
////////////////////////////////////////

  int ans = 0;
  while (true) {
    int sum = 0;
    REP(i, m) REP(j, n) {
      if (c[i][j] != '.') continue;
      vector<int> x(4, i), y(4, j);
      REP(k, 4) {
        while (true) {
          if (c[x[k]][y[k]] != '.') break;
          int tx = x[k] + dx[k];
          int ty = y[k] + dy[k];
          if (tx < 0 || m <= tx || ty < 0 || n <= ty) break;
          x[k] += dx[k];
          y[k] += dy[k];
        }
      }
      REP(k, 4) REP(l, 4) {
        if (k >= l) continue;
        if (c[x[k]][y[k]] != '.' && c[x[k]][y[k]] == c[x[l]][y[l]]) {
          sum += 2;
          c[x[k]][y[k]] = '.';
          c[x[l]][y[l]] = '.';
        }
      }
    }
    if (sum == 0) break;
    ans += sum;
  }
  cout << ans << endl;
}

