using namespace std;
typedef pair<int, int> P;

void solve()
{
	int M, N;
	while(cin >> M >> N)
	{
		queue<P> Que;
		vector< vector<char> > Field;
		Field.resize(M);
		for(int i = 0; i < M; ++i)
		{
			Field[i].resize(N);
		}
		for(int i = 0; i < M; ++i)
		{
			for(int j = 0; j < N; ++j)
			{
				cin >> Field[i][j];
				if(Field[i][j] == '.')
				{
					Que.push(make_pair<int, int>(i, j));
				}
			}
		}
		int count = 0;
		while(!Que.empty())
		{
			P up    = make_pair(Que.front().first - 1, Que.front().second);
			P down  = make_pair(Que.front().first + 1, Que.front().second);
			P left  = make_pair(Que.front().first, Que.front().second - 1);
			P right = make_pair(Que.front().first, Que.front().second + 1);
			bool upOK = false;
			bool downOK = false;
			bool leftOK = false;
			bool rightOK = false;
			while(0 <= up.first && up.first < M)
			{
				if(Field[up.first][up.second] == '.')
				{
					--up.first;
				}
				else
				{
					upOK = true;
					break;
				}
			}
			while(0 <= down.first && down.first < M)
			{
				if(Field[down.first][down.second] == '.')
				{
					++down.first;
				}
				else
				{
					downOK = true;
					break;
				}
			}
			while(0 <= left.second && left.second < N)
			{
				if(Field[left.first][left.second] == '.')
				{
					--left.second;
				}
				else
				{
					leftOK = true;
					break;
				}
			}
			while(0 <= right.second && right.second < N)
			{
				if(Field[right.first][right.second] == '.')
				{
					++right.second;
				}
				else
				{
					rightOK = true;
					break;
				}
			}
			if(upOK && downOK && Field[up.first][up.second] == Field[down.first][down.second] && Field[up.first][up.second] != '.')
			{
				count += 2;
				Field[up.first][up.second] = '.';
				Field[down.first][down.second] = '.';
				Que.push(make_pair<int, int>(Que.front().first, Que.front().second));
				Que.push(make_pair<int, int>(up.first, up.second));
				Que.push(make_pair<int, int>(down.first, down.second));
			}
			if(upOK && leftOK && Field[up.first][up.second] == Field[left.first][left.second] && Field[left.first][left.second] != '.')
			{
				count += 2;
				Field[up.first][up.second] = '.';
				Field[left.first][left.second] = '.';
				Que.push(make_pair<int, int>(Que.front().first, Que.front().second));
				Que.push(make_pair<int, int>(up.first, up.second));
				Que.push(make_pair<int, int>(left.first, left.second));
			}
			if(upOK && rightOK && Field[up.first][up.second] == Field[right.first][right.second] && Field[right.first][right.second] != '.')
			{
				count += 2;
				Field[up.first][up.second] = '.';
				Field[right.first][right.second] = '.';
				Que.push(make_pair<int, int>(Que.front().first, Que.front().second));
				Que.push(make_pair<int, int>(up.first, up.second));
				Que.push(make_pair<int, int>(right.first, right.second));
			}
			if(leftOK && downOK && Field[left.first][left.second] == Field[down.first][down.second] && Field[left.first][left.second] != '.')
			{
				count += 2;
				Field[left.first][left.second] = '.';
				Field[down.first][down.second] = '.';
				Que.push(make_pair<int, int>(Que.front().first, Que.front().second));
				Que.push(make_pair<int, int>(left.first, left.second));
				Que.push(make_pair<int, int>(down.first, down.second));
			}
			if(leftOK && rightOK && Field[left.first][left.second] == Field[right.first][right.second] && Field[right.first][right.second] != '.')
			{
				count += 2;
				Field[left.first][left.second] = '.';
				Field[right.first][right.second] = '.';
				Que.push(make_pair<int, int>(Que.front().first, Que.front().second));
				Que.push(make_pair<int, int>(left.first, left.second));
				Que.push(make_pair<int, int>(right.first, right.second));
			}
			if(rightOK && downOK && Field[right.first][right.second] == Field[down.first][down.second] && Field[right.first][right.second] != '.')
			{
				count += 2;
				Field[right.first][right.second] = '.';
				Field[down.first][down.second] = '.';
				Que.push(make_pair<int, int>(Que.front().first, Que.front().second));
				Que.push(make_pair<int, int>(right.first, right.second));
				Que.push(make_pair<int, int>(down.first, down.second));
			}
			Que.pop();
		}
		cout << count << endl;
	}
}

