char a[502][502],X='#',C='.';
int F(int x,int y)
{
	if(a[y][x]-C)return 0;
	char*p,c[4],t;
	int d[]={0,1,0,-1,0},e[4],i,j,r=0;
	for(i=0;i<4;++i)
	{
		for(j=1;t=a[y+j*d[i]][x+j*d[i+1]],t==C;++j);
		c[i]=t;
		e[i]=j;
	}
	for(i=0;i<3;++i)
	{
		if(c[i]-X && (p=std::find(c+i+1,c+4,c[i]))<c+4)
		{
			a[y+e[i]*d[i]][x+e[i]*d[i+1]]=C;
			j=p-c;
			a[y+e[j]*d[j]][x+e[j]*d[j+1]]=C;
			r+=2;
		}
	}
	return r;
}
int main()
{
	int m,n,x,y,f,r,t;
	scanf("%d%d",&m,&n);
	for(y=1;y<=m;++y)scanf("%s",&a[y][1]);
	for(y=0;y<m+2;++y)a[y][0]=a[y][n+1]=X;
	for(r=x=0;x<n+2;++x)a[0][x]=a[m+1][x]=X;
	do
	{
		f=0;
		for(y=1;y<=m;++y)for(x=1;x<=n;++x)if(t=F(x,y))f=1,r+=t;
	}while(f);
	printf("%d\n",r);
	return 0;
}

