using namespace std;

const int MAX_W = 501;
int h, w;
char C[MAX_W][MAX_W];
int dx[4] = {0,0,-1,1};
int dy[4] = {-1,1,0,0};
bool memo[MAX_W][MAX_W];

struct P{
	int x, y;
	P(int x_, int y_){ x = x_; y = y_; }
};

bool search(int ax, int ay, int bx, int by){
	for(int y=0 ; y < h ; y++ ){
		for(int x=0 ; x < w ; x++ ){
			memo[y][x] = false;
		}
	}
	for(int i=0 ; i < 4 ; i++ ){
		int mx = ax + dx[i];
		int my = ay + dy[i];
		while( true ){
			if( mx < 0 || my < 0 || w <= mx || h <= my ) break;
			if( C[my][mx] == '.' ){
				memo[my][mx] = true;
			}else{
				break;
			}
			mx += dx[i];
			my += dy[i];
		}
	}
	for(int i=0 ; i < 4 ; i++ ){
		int mx = bx + dx[i];
		int my = by + dy[i];
		while( true ){
			if( mx < 0 || my < 0 || w <= mx || h <= my ) break;
			if( memo[my][mx] ){
				return true;
			}else if( C[my][mx] != '.' ){
				break;
			}
			mx += dx[i];
			my += dy[i];
		}
	}
	return false;
}

int solve(){
	vector<P> v[26];
	for(int y=0 ; y < h ; y++ ){
		for(int x=0 ; x < w ; x++ ){
			if( C[y][x] != '.' ){
				v[C[y][x]-'A'].push_back(P(x,y));
			}
		}
	}
	int res = 0;
	while( true ){
		bool change = false;
		for(int i=0 ; i < 26 ; i++ ){
			if( v[i].size() == 2 ){
				if( search(v[i][0].x, v[i][0].y, v[i][1].x, v[i][1].y) ){
					C[v[i][0].y][v[i][0].x] = '.';
					C[v[i][1].y][v[i][1].x] = '.';
					v[i].erase(v[i].begin(),v[i].end());
					change = true;
					res += 2;
				}
			}
		}
		if( !change ) break;
	}
	return res;
}

