#define REP(i, a, n) for(int i = ((int) a); i < ((int) n); i++)
using namespace std;

int M, N;
char S[550][550];
vector<int> Y[256], X[256];
bool E[256][256];
bool b[256];

void dfs(int v) {
  if(!b[v]) return;
  b[v] = false;
  REP(i, 'A', 'Z' + 1) if(E[v][i]) dfs(i);
}

int main(void) {
  cin >> M >> N;
  REP(i, 0, M) cin >> S[i];

  REP(i, 0, M) REP(j, 0, N) if(S[i][j] != '.') {
    Y[S[i][j]].push_back(i);
    X[S[i][j]].push_back(j);
  }

  REP(i, 0, 256) REP(j, 0, 256) E[i][j] = false;

  REP(i, 'A', 'Z' + 1) {
    if(Y[i].size() == 0) continue;
    int x1 = X[i][0], x2 = X[i][1];
    int y1 = Y[i][0], y2 = Y[i][1];
    if(x1 == x2) {
      REP(j, min(y1, y2) + 1, max(y1, y2)) {
        if(S[y1][j] != '.') E[i][S[j][x1]] = true;
      }
    } else if(y1 == y2) {
      REP(j, min(x1, x2) + 1, max(x1, x2)) {
        if(S[j][x1] != '.') E[i][S[y1][j]] = true;
      }
    } else {
      if(S[y1][x2] != '.' && S[y1][x2] == S[y2][x1]) E[i][S[y1][x2]] = true;
    }
  }

  vector<int> fi;
  REP(i, 'A', 'Z' + 1) {
    if(Y[i].size() == 0) continue;
    int x1 = X[i][0], x2 = X[i][1];
    int y1 = Y[i][0], y2 = Y[i][1];
    if(x1 == x2 && abs(y1 - y2) == 1) fi.push_back(i);
    if(y1 == y2 && abs(x1 - x2) == 1) fi.push_back(i);
  }
  REP(i, 'A', 'Z' + 1) REP(j, i + 1, 'Z' + 1) if(i != j) {
    if(E[i][j] && E[j][i]) {
      fi.push_back(i);
      fi.push_back(j);
    }
  }

  REP(i, 'A', 'Z' + 1) if(Y[i].size() > 0) b[i] = true;
  REP(i, 0, fi.size()) dfs(fi[i]);

  int ans = 0;
  REP(i, 'A', 'Z' + 1) if(b[i]) ans += 2;
  cout << ans << endl;

  return 0;
}

