  vector<string> fi(m);
  REP(i,m) cin >> fi[i];
  int answer=0;
  REP(i,m) REP(j,n) if(fi[i][j]=='.') --answer;
  vector<bool> exi(m,false),exj(n,false);
  REP(i,m) REP(j,n) if(fi[i][j]!='.'){
    exi[i]=true;
    exj[j]=true;
  }
  bool flag=true;
  while(flag){
    REP(i,m){
      REP(j,n){
	if(!exi[i]&&!exj[j]) continue;
	if(fi[i][j]!='.') continue;
	vector<int> x,y;
	for(int k=i-1;k>=0;--k){
	  if(fi[k][j]!='.'){
	    x.push_back(k);
	    y.push_back(j);
	    break;
	  }
	}
	for(int k=i+1;k<m;++k){
	  if(fi[k][j]!='.'){
	    x.push_back(k);
	    y.push_back(j);
	    break;
	  }
	}
	for(int k=j-1;k>=0;--k){
	  if(fi[i][k]!='.'){
	    x.push_back(i);
	    y.push_back(k);
	    break;
	  }
	}
	for(int k=j+1;k<n;++k){
	  if(fi[i][k]!='.'){
	    x.push_back(i);
	    y.push_back(k);
	    break;
	  }
	}
	REP(k,x.size()) REP(l,k){
	  if(fi[x[k]][y[k]]==fi[x[l]][y[l]]){
	    fi[x[k]][y[k]]='.';
	    fi[x[l]][y[l]]='.';
	    flag=true;
	  }
	}
      }
    }
  }
  REP(i,m) REP(j,n) if(fi[i][j]=='.') ++answer;
  cout << answer << endl;

  return 0;
}

