bool dire(PII a1, PII a2) {
	int dx = a1.X - a2.X;
	int dy = a1.Y - a2.Y;

	return (dx < 0 && dy < 0) ||
		   (dx > 0 && dy > 0);
}

bool can_erase(char c0) {
	PII a1 = poss[c0][0];
	PII a2 = poss[c0][1];

	if ((abs(a1.X - a2.X) + abs(a1.Y - a2.Y)) == 1) {
		return false;
	}

	int min_x = min(a1.X, a2.X);
	int max_x = max(a1.X, a2.X);
	int min_y = min(a1.Y, a2.Y);
	int max_y = max(a1.Y, a2.Y);
	bool rev = dire(a1, a2);

	for (int i = 0; i < 2; i++) {
		bool ans = [&]()->bool{
			{
				int y = i ? min_y : max_y;
				for (int x = min_x; x <= max_x; x++) {
					char c = ts[y][x];
					if (c == '.') continue;
					if (c == c0) continue;
					if (as[c - 'A']) {
						return false;
					}
				}
			}

			{
				int x = (!!i ^ rev) ? min_x : max_x;
				for (int y = min_y; y <= max_y; y++) {
					char c = ts[y][x];
					if (c == '.') continue;
					if (c == c0) continue;
					if (as[c - 'A']) {
						return false;
					}
				}
			}

			return true;
		}();

		if (ans) return true;
	}

	return false;
}

bool erase(char c) {
	if (!as[c - 'A']) return false;
	if (!can_erase(c)) return false;
	as[c - 'A'] = false;

	return true;
}

