using namespace std;

int H, W;
const int MAX_W = 500;

char board[MAX_W][MAX_W + 1];
bool is_remain[26];

struct point{
	int y, x;
};

vector<point> ps[26];

int dist(point a, point b){
	return abs(a.y - b.y) + abs(a.x - b.x);
}

int between(int a, int b, int x){
	return min(a, b) <= x && x <= max(a, b);
}

bool check1(point a, point b, point p){
	return a.x != p.x && !between(a.y, b.y, p.y);
}

bool check2(point a, point b, point p){
	return a.y != p.y && !between(a.x, b.x, p.x);
}

bool check3(point a, point b, point p){
	return ( !( a.x == p.x && between(a.y, b.y, p.y) ) )
			&& ( !( b.y == p.y && between(a.x, b.x, p.x) ) );
}

bool check4(point a, point b, point p){
	return ( !( a.y == p.y && between(a.x, b.x, p.x) ) )
			&& ( !( b.x == p.x && between(a.y, b.y, p.y) ) );
}


int solve(){
	int ans = 0;

	for(;;){
		bool update = false;

		for(int i=0; i<26; i++)if(is_remain[i] && dist(ps[i][0], ps[i][1]) != 1){
			bool ok = false;
			point& a = ps[i][0], b = ps[i][1];
			//
			if(a.x == b.x){
				bool ok2 = true;
				for(int j=0; j<26; j++)if(i!=j && is_remain[j]){
					ok2 &= check1(a, b, ps[j][0]);
					ok2 &= check1(a, b, ps[j][1]);
				}
				ok |= ok2;
			}
			if(a.y == b.y){
				bool ok2 = true;
				for(int j=0; j<26; j++)if(i!=j && is_remain[j]){
					ok2 &= check2(a, b, ps[j][0]);
					ok2 &= check2(a, b, ps[j][1]);
				}
				ok |= ok2;
			}

			//
			{
				bool ok2 = true;
				for(int j=0; j<26; j++)if(i!=j && is_remain[j]){
					ok2 &= check3(a, b, ps[j][0]);
					ok2 &= check3(a, b, ps[j][1]);
				}
				ok |= ok2;
			}
			//
			{
				bool ok2 = true;
				for(int j=0; j<26; j++)if(i!=j && is_remain[j]){
					ok2 &= check4(a, b, ps[j][0]);
					ok2 &= check4(a, b, ps[j][1]);
				}
				ok |= ok2;

			}

			if(ok){
				is_remain[i] = false;
				ans += 2;
