typedef vector<pii> vp;
typedef vector<vp> vvp;
int main(){
	int m,n;
	cin>>m>>n;
	vs f(m);
	const int num_alph='Z'-'A'+1;
	vvp pos(num_alph);
	REP(i,m){
		cin>>f[i];
		REP(j,n){
			if(isalpha(f[i][j])){
				pos[f[i][j]-'A'].push_back(make_pair(i,j));
			}
		}
	}
	FOR(c,0,num_alph){
		sort(ALL(pos[c]));
	}

	REP(c,num_alph){
		if(pos[c].size()){
			if(abs(pos[c][0].first-pos[c][1].first)+abs(pos[c][0].second-pos[c][1].second)<=1){
				pos[c].clear();
			}
		}
	}
	int ans=0;
	bool changed=true;
	while(changed){
		changed=false;
		FOR(c,0,num_alph){
			if(pos[c].size()==0){
				continue;
			}
			bool left_ok=true;
			bool right_ok=true;
			int x_left=pos[c][0].second;
			int x_right=pos[c][1].second;
			if(x_left>x_right)swap(x_left,x_right);
			int y_top=pos[c][0].first;
			int y_bottom=pos[c][1].first;
			FOR(y,y_top,y_bottom+1){
				if(f[y][x_left]!='.'&&f[y][x_left]!='A'+c){
					left_ok=false;
				}
				if(f[y][x_right]!='.'&&f[y][x_right]!='A'+c){
					right_ok=false;
				}
			}
			bool top_ok=true;
			bool bottom_ok=true;
			FOR(x,x_left,x_right+1){
				if(f[y_top][x]!='.'&&f[y_top][x]!='A'+c){
					top_ok=false;
				}
				if(f[y_bottom][x]!='.'&&f[y_bottom][x]!='A'+c){
					bottom_ok=false;
				}
			}
			bool ok=false;
			if(pos[c][0].second<pos[c][1].second){
				ok=((top_ok&&right_ok)||(bottom_ok&&left_ok));
			}else{
				ok=((top_ok&&left_ok)||(bottom_ok&&right_ok));
			}

			if(ok){
				f[pos[c][0].first][pos[c][0].second]='.';
				f[pos[c][1].first][pos[c][1].second]='.';
				pos[c].clear();
				ans+=2;
				changed=true;
			}
		}
	}
	cout<<ans<<endl;
}

