const int INF = 1 << 28;
char board[510][510];
int dx[] = {0, 1, 0, -1};
int dy[] = {1, 0, -1, 0};
int main() {
    int H, W; scanf("%d%d", &H, &W);
    int rest = 0;
    for(int i=0; i<H; i++) {
        for(int j=0; j<W; j++) {
            scanf(" %c", &board[i][j]);
            if(isupper(board[i][j])) rest++;
        }
    }

    int ans = 0;
    while(1) {
        bool ok = false;
        pii INIT = make_pair(-INF, -INF);
        for(int i=0; i<H; i++) {
            for(int j=0; j<W; j++) {
                vector<pii> rec(26, INIT);
                if(isupper(board[i][j])) continue;
                for(int k=0; k<4; k++) {
                    int x = i, y = j;
                    while(1) {
                        x += dx[k], y += dy[k];
                        if(x < 0 || x >= H || y < 0 || y >= W) break;
                        if(isupper(board[x][y])) {
                            int c = board[x][y] - 'A';
                            if(rec[c] == INIT) rec[c] = make_pair(x, y);
                            else {
                                int u = rec[c].first, v = rec[c].second;
                                // printf("found: (%d, %d) and (%d, %d): %c (%d, %d)\n", u, v, x, y, 'A' + c, i, j);
                                board[u][v] = board[x][y] = '.';
                                rec[c] = INIT;
                                ok = true;
                                ans += 2;
                            }
                            break;
                        }
                    }
                }
            }
        }
        if(!ok) break;
    }
    printf("%d\n", ans);
    return 0;
}

