bool opt_debug = false;

int main(int argc, char** argv) {
	std::ios_base::sync_with_stdio(false);
	// set options {{{
	int __c;
	while ( (__c = getopt(argc, argv, "d")) != -1 ) {
		switch (__c) {
			case 'd':
				opt_debug = true;
				break;
			default:
				abort();
		}
	}
	// }}}

	// opt_debug = true;
	// input("./inputs/2.txt");
	// output("./outputs/0.txt");

	int H, W; cin >> H >> W;
	VVC field(H, VC(W));
	ULL remaining = 0;
	REP (i, H) {
		REP (j, W) {
			cin >> field[i][j];
			if (field[i][j] != '.') {
				remaining |= 1 << (field[i][j] - 'A');
			}
		}
	}

	// y compression
	bool prev_empty = false;
	REP (i, H) {
		bool empty = true;
		REP (j, W) {
			if (field[i][j] != '.') {
				empty = false;
				break;
			}
		}
		if (prev_empty && empty) {
			field.erase(field.begin() + i);
			H--;
			i--;
		}
		prev_empty = empty;
	}

	// x compression
	prev_empty = false;
	REP (j, W) {
		bool empty = true;
		REP (i, H) {
			if (field[i][j] != '.') {
				empty = false;
				break;
			}
		}
		dump(j);
		dump(prev_empty);
		dump(empty);
		if (prev_empty && empty) {
			REP (i, H) {
				field[i].erase(field[i].begin() + j);
			}
			W--;
			j--;
		}
		prev_empty = empty;
	}

	int score = 0;
	while (remaining) {
		bool hit = false;
		REP (i, H) {
			REP (j, W) {
				if (field[i][j] != '.') { continue; }
				vector< pair<P, char> > stats;
				REP (d, 4) {
					P cur = P(i, j);
					while (in_field(H, W, cur + dydx4[d]) && field[cur.Y][cur.X] == '.') {
						cur += dydx4[d];
					}
					if (field[cur.Y][cur.X] != '.') {
						stats.PB(MP(cur, field[cur.Y][cur.X]));
					}
				}
				REP (k, stats.size()) {
					FOR (l, k+1, stats.size()) {
						pair<P, char> a = stats[k], b = stats[l];
						if (a.S == b.S) {
							hit = true;
							score += 2;
							char c = a.S;
							remaining &= ~(1 << (c - 'A'));
							field[a.F.Y][a.F.X] = field[b.F.Y][b.F.X] = '.';
						}
					}
				}
			}
		}

		if (!hit) {
			break;
		}
	}

	cout << score << endl;

	return 0;
}

