const int dx[] = {-1,0,1,0}, dy[] = {0,1,0,-1};

int H, W;

int main() {
    cin >> H >> W;
    vvc field(H, vc(W));
    vvll state(H, vll(W));
    REP(y, H) REP(x, W) {
        cin >> field[y][x];
    }

    vector<vector<pii>> char2pos(26, vector<pii>(2));
    vb seen(26);
    REP(y, H) REP(x, W) {
        if (field[y][x] != '.') {
            int c = field[y][x] - 'A';
            if (seen[c]) {
                char2pos[c][1] = {x, y};
                field[y][x] += 0x20;
            } else {
                char2pos[c][0] = {x, y};
                seen[c] = true;
            }
        }
    }

    int score = 0;
    bool redo;
    do {
        redo = false;

        REP(y, H) REP(x, W) {
            if (field[y][x] != '.') {
                ll s = 1LL << (field[y][x] - 'A');
                REP(i, 4) {
                    int sx = x + dx[i], sy = y + dy[i];
                    while (0 <= sx && sx < W && 0 <= sy && sy < H &&
                        field[sy][sx] == '.') {
                        state[sy][sx] |= s;

                        sx += dx[i];
                        sy += dy[i];
                    }
                }
            }
        }

        REP(y, H) REP(x, W) {
            if (field[y][x] == '.') {
                REP(c, 27) {
                    if (seen[c]) {
                        ll s = (1LL<<c) | (1LL<<(c+0x20));
                        if ((state[y][x] & s) == s) {
                            int sx, sy;
                            sx = char2pos[c][0].first;
                            sy = char2pos[c][0].second;
                            field[sy][sx] = '.';
                            sx = char2pos[c][1].first;
                            sy = char2pos[c][1].second;
                            field[sy][sx] = '.';
                            seen[c] = false;
                            score += 2;
                            redo = true;
                        }
                    }
                }
            }
        }
    } while (redo);
    cout << score << endl;
}

