using namespace std;
typedef pair<int,int> P;
int res=0;
int m,n;
int dx[4]={1,0,-1,0},dy[4]={0,1,0,-1};
map<int,vector<P>> pos;

bool check(vector<string> &s, int sx,int sy){
    map<int,int> mp;
    int gx,gy;
    bool f=false;
    for(int i=0;i<4;i++){
        gx=sx,gy=sy;
        while(true){
            gx += dx[i]; gy += dy[i];
            if(gx<0 || m<=gx || gy<0 || n<=gy) break;
            if(s[gx][gy]=='.') continue;
            int idx=s[gx][gy]-'A';
            if(mp[idx]){
                res+=2;
                f=true;
                P p1=pos[idx][0],p2=pos[idx][1];
                s[p1.first][p1.second]='.';
                s[p2.first][p2.second]='.';
            }
            else mp[idx]++;
            break;
        }
    }
    return f;
}
int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);

    cin>>m>>n;
    vector<string> s(m);
    for(int i=0;i<m;i++){
        cin>>s[i];
        for(int j=0;j<n;j++){
            if(s[i][j]=='.') continue;
            int idx=s[i][j]-'A';
            pos[idx].push_back(P(i,j));
        }
    }
    while(true){
        bool f=false;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(s[i][j]!='.') continue;
                f |= check(s,i,j);
            }
        }
        if(!f) break;
    }
    cout<<res<<endl;
}

