#include<iostream>
using namespace std;

string grid[500];
int posy[26][2],posx[26][2];
int pos[26][2][2];
bool use[26];
int n,m;
int res;

bool cluck(int y,int x){
  int k;
  int cnt[26];
  for(int i=0;i<26;i++)cnt[i] = 0;

  k = 1;
  while(y+k<n && grid[y+k][x] == '.')k++;
  if(y+k<n)cnt[grid[y+k][x]-'A']++;

  k = 1;
  while(x+k<m && grid[y][x+k] == '.')k++;
  if(x+k<m)cnt[grid[y][x+k]-'A']++;

  k = 1;
  while(y-k>=0 && grid[y-k][x] == '.')k++;
  if(y-k>=0)cnt[grid[y-k][x]-'A']++;

  k = 1;
  while(x-k>=0 && grid[y][x-k] == '.')k++;
  if(x-k>=0)cnt[grid[y][x-k]-'A']++;   

  bool f = false;
  for(int i=0;i<26;i++){
    if(cnt[i]==2){
      res += 2;
      grid[posy[i][0]][posx[i][0]] ='.';
      grid[posy[i][1]][posx[i][1]] ='.';
      use[i] = false;
      f = true;
    }
  }
  return f;
}

int main(){
  cin >> n >> m;
  for(int i=0;i<n;i++)cin >> grid[i];

  for(int i=0;i<26;i++){
    posy[i][0] = -1;
    posx[i][0] = -1;
    posy[i][1] = -1;
    posx[i][1] = -1;
    use[i] = false;
  }

  for(int i=0;i<n;i++){
    for(int j=0;j<m;j++){
      if(grid[i][j] != '.'){
	if(posy[grid[i][j]-'A'][0]<0){
	  posy[grid[i][j]-'A'][0] = i;
	  posx[grid[i][j]-'A'][0] = j;
	  use[grid[i][j]-'A'] = true;
	}else{
	  posy[grid[i][j]-'A'][1] = i;
	  posx[grid[i][j]-'A'][1] = j;
	}
      }
    }
  }

  res = 0;
  while(1){
    bool f = false;
    for(int i=0;i<26;i++){
      if(use[i]){
	if(posy[i][0] == posy[i][1]){
	  for(int j=posx[i][0]+1;j<posx[i][1];j++){
	    if(grid[posy[i][0]][j]=='.')f |= cluck(posy[i][0],j);
	  }
	}else if(posx[i][0] == posx[i][1]){
	  for(int j=posy[i][0]+1;j<posy[i][1];j++){
	    if(grid[j][posx[i][0]]=='.')f |= cluck(j,posx[i][0]);
	  }
	}else{
	  if(grid[posy[i][0]][posx[i][1]] == '.')f |= cluck(posy[i][0],posx[i][1]);
	  if(grid[posy[i][1]][posx[i][0]] == '.')f |= cluck(posy[i][1],posx[i][0]);
	}
      }
    }
    if(!f)break;
  }

  cout << res << endl;
}

