							pickx[k]=x;
							picky[k]=y;
						}
					}
					for(int k=0;k<4;k++){
						for(int l=k+1;l<4;l++){
							if(pick[k]==pick[l] && pick[k]!=0){
								score+=2;
								fie[pickx[k]][picky[k]]=0;
								fie[pickx[l]][picky[l]]=0;
								flag=true;
							}
						}
					}
				}
			}
		}
	}
	printf("%d\n",score);
	return 0;
}

