		if(b[i][j]!='.')continue;
		//dbg(i);dbg(j);
		vector<P> tile;
		vector<P> savt;
		vector<char> savc;
		int x1,x2;
		x1=x2=j;
		int y1,y2;
		y1=y2=i;
		int point=0;
		while(x1>=0&&b[i][x1]=='.')x1--;
		if(x1>=0)tile.pb(P(i,x1));
