		rep(k,tile.size())rep(l,k){
			P t1=tile[k],t2=tile[l];
			if(b[t1.fi][t1.se]!='.'&&b[t1.fi][t1.se]==b[t2.fi][t2.se]){
				savt.pb(t1);
				savt.pb(t2);
				savc.pb(b[t1.fi][t1.se]);
				b[t1.fi][t1.se]=b[t2.fi][t2.se]='.';
				point+=2;
