          }
        }
        rrep2(j, x, -1) {
          if (C[y][j] != '.') {
            cnt[C[y][j] - 'A']++;
            break;
          }
        }
        rep(j, 26) {
          if (cnt[j] == 2) {
            //cerr << char(j + 'A') << endl;
            update = true;
            ans += 2;
            check[j] = true;
            vector<pii> tmppos = pos[char(j + 'A')];
            for (auto p : tmppos) {
              C[p.fi][p.se] = '.';
            }
            /*
            rep(i, m) {
