const double EPS = 1e-10;
const double PI  = acos(-1.0);

int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  int H, W; cin >> H >> W;
  VS vs(H);
  REP(i,H) cin >> vs[i];

  vector<PII> memo(32, MP(-1,-1));
  vector<pair<PII,PII>> pos;
  REP(y,H) REP(x,W){
	if(vs[y][x] != '.'){
	  int i = vs[y][x] - 'A';
	  if(memo[i].first < 0)
		memo[i] = MP(x,y);
	  else
		pos.PB(MP(memo[i], MP(x,y)));
	}
  }

  int ans = 0;
  bool up  = true;
  while(up){
	up = false;

	for(int i=0;i<SZ(pos);++i){
	  PII p1 = pos[i].first, p2 = pos[i].second;
	  if(p1 > p2) swap(p1, p2);
	  int mx = min(p1.first, p2.first);
	  int Mx = max(p1.first, p2.first);
	  int my = min(p1.second, p2.second);
	  int My = max(p1.second, p2.second);
	  if((mx+1 == Mx && my == My)
		 || (mx == Mx && my+1 == My)) continue;

	  int ok = 0;
	  if(p1.second <= p2.second)
	  for(int j=0;j<SZ(pos);++j){
		if(i == j) continue;
		PII piis[2] = {pos[j].first, pos[j].second};
		for(auto pii:piis){
		  int x = pii.first, y = pii.second;
		  if((y == my && mx <= x && x <= Mx)
			 || (x == Mx && my <= y && y <= My))
			ok |= 1;
		  if((y == My && mx <= x && x <= Mx)
			 || (x == mx && my <= y && y <= My))
			ok |= 2;
		}
	  }
	  else
	  for(int j=0;j<SZ(pos);++j){
		if(i == j) continue;
		PII piis[2] = {pos[j].first, pos[j].second};
		for(auto pii:piis){
		  int x = pii.first, y = pii.second;
		  if((y == my && mx <= x && x <= Mx)
			 || (x == mx && my <= y && y <= My))
			ok |= 1;
		  if((y == My && mx <= x && x <= Mx)
			 || (x == Mx && my <= y && y <= My))
			ok |= 2;
		}
	  }

	  if(ok != 3){
		pos.erase(pos.begin() + i);
		ans += 2;
		up = true;
		break;
	  }
	}
  }

  cout << ans << endl;

  return 0;
}

