#define REP(i, a, n) for(int i = ((int) a); i < ((int) n); i++)
using namespace std;

int M, N, K;
char S[550][550];
int h, w;
vector<int> y, x;
vector<char> c;
char s[100][100];
int dd[5] = { 0, 1, 0, -1, 0 };

int compress(vector<int> &a) {
  vector<int> v;

  REP(i, 0, a.size()) v.push_back(a[i]);
  sort(v.begin(), v.end());
  v.erase(unique(v.begin(), v.end()), v.end());
  REP(i, 0, a.size()) a[i] = find(v.begin(), v.end(), a[i]) - v.begin();

  return (int) v.size();
}

bool isin(int y, int x) {
  return 0 <= y && y < h && 0 <= x && x < w;
}

int main(void) {
  cin >> M >> N;
  K = 0;
  REP(i, 0, M) REP(j, 0, N) {
    cin >> S[i][j];
    if(S[i][j] != '.') {
      y.push_back(i);
      x.push_back(j);
      c.push_back(S[i][j]);
      K++;
    }
  }

  h = compress(y);
  w = compress(x);

  REP(i, 0, h) REP(j, 0, w) s[i][j] = '.';
  REP(i, 0, K) {
    // cout << y[i] << ", " << x[i] << ": " << c[i] << endl;
    s[y[i]][x[i]] = c[i];
  }

  // REP(i, 0, h) {
  //   REP(j, 0, w) cout << s[i][j];
  //   cout << endl;
  // }

  int ans = 0;
  bool visited[256];
  REP(i, 0, 256) visited[i] = false;
  bool f = true;
  while(f) {
    f = false;
    REP(i, 0, h) REP(j, 0, w) {
      if(s[i][j] == '.' || visited[s[i][j]]) {
        int cnt[256];
        REP(k, 0, 256) cnt[k] = 0;
        // cout << i << ", " << j << endl;
        REP(k, 0, 4) {
          int ny = i, nx = j;
          while(isin(ny, nx)) {
            // cout << "  " << ny << ", " << nx << ": " << s[ny][nx] << endl;
            if(s[ny][nx] != '.' && !visited[s[ny][nx]]) {
              // cout << s[ny][nx] << endl;
              cnt[s[ny][nx]]++;
              if(cnt[s[ny][nx]] == 2) {
                // cout << "  vanish: " << S[ny][nx] << endl;
                visited[s[ny][nx]] = true;
                ans += 2;
                f = true;
              }
              break;
            }
            ny += dd[k];
            nx += dd[k + 1];
          }
        }
      }
    }
  }
  cout << ans << endl;

  return 0;
}

