        for(int x = min(vx[c][0], vx[c][1]); x <= max(vx[c][0], vx[c][1]); x++) if(grid[vy[c][1]][x] != '.' && grid[vy[c][1]][x] != (c + 'A')) ok = false;
        for(int y = min(vy[c][0], vy[c][1]); y <= max(vy[c][0], vy[c][1]); y++) if(grid[y][vx[c][0]] != '.' && grid[y][vx[c][0]] != (c + 'A')) ok = false;
        if(ok) goto GO;

        ok = true;
        for(int x = min(vx[c][0], vx[c][1]); x <= max(vx[c][0], vx[c][1]); x++) if(grid[vy[c][0]][x] != '.' && grid[vy[c][0]][x] != (c + 'A')) ok = false;
        for(int y = min(vy[c][0], vy[c][1]); y <= max(vy[c][0], vy[c][1]); y++) if(grid[y][vx[c][1]] != '.' && grid[y][vx[c][1]] != (c + 'A')) ok = false;
        if(!ok) continue;

      GO:
        //cout<<(char)(c + 'A')<<endl;
        grid[vy[c][1]][vx[c][1]] = '.';
        grid[vy[c][0]][vx[c][0]] = '.';
        memset(vx[c], -1, sizeof(vx[c]));
        memset(vy[c], -1, sizeof(vy[c]));
        ans += 2;
        update = true;
      }
    }
    cout<<ans<<endl;
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
typedef unsigned int uint;

char C[500][500];
struct P { int row, col; };

int idx(char c) {return c - 'A'; };
bool is_adjacent(P a, P b) { return abs(a.row - b.row) + abs(a.col - b.col) == 1; }

uint between(P a, P b, bool diag) {
    if (diag) swap(a, b);
    uint ret = 0;
    int d1 = (b.row >= a.row) ? 1 : -1;
    for (int j = a.row; j != b.row + d1; j += d1) {
        if (C[j][a.col] == '.' || C[j][a.col] == C[a.row][a.col]) continue;
        ret |= 1LL << idx(C[j][a.col]);
    }
    int d2 = (b.col >= a.col) ? 1 : -1;
    for (int j = a.col; j != b.col + d2; j += d2) {
        if (C[b.row][j] == '.' || C[b.row][j] == C[a.row][a.col]) continue;
        ret |= 1LL << idx(C[b.row][j]);
    }
    return ret;
}

int main() {
    int M, N;
    cin >> M >> N;
    vector<P> pos[26];
    for (int i = 0; i < M; ++i) {
        for (int j = 0; j < N; ++j) {
            cin >> C[i][j];
            if (C[i][j] != '.') { pos[idx(C[i][j])].push_back((P){i, j}); }
        }
    }

    uint req[26][2];  // req[i][j] := i
    for (int i = 0; i < 26; ++i) {
        if (pos[i].empty() || is_adjacent(pos[i][0], pos[i][1])) {
            req[i][0] = req[i][1] = -1;
            continue;
        }
        req[i][0] = between(pos[i][0], pos[i][1], false);
        req[i][1] = between(pos[i][0], pos[i][1], true);
    }

    uint has = 0;  // 
    while (1) {
        bool update = false;
        for (int i = 0; i < 26; ++i) {
            if ((has >> i) & 1) continue;
            for (int j = 0; j < 2; ++j) {
                if ((req[i][j] & has) == req[i][j]) {
                    has |= (1LL << i);
                    update = true;
                    break;
                }
            }
        }
        if ( ! update) break;
    }
    cout << __builtin_popcountll(has) * 2 << endl;
    return 0;
}

