bool canReach_x(char C, int x, int y1, int y2){
  if(y1 > y2) swap(y1, y2);
  for(int i = y1+1 ; i < y2 ; i++){
    if(field[i][x] != '.') return false;
  }
  return true;
}

bool canReach_y(char C, int y, int x1, int x2){
  if(x1 > x2) swap(x1, x2);
  for(int j = x1+1 ; j < x2 ; j++){
    if(field[y][j] != '.') return false;
  }
  return true;
}

bool canReach(char C, int cx, int cy, int x, int y){
  if(cx == x){
    if(!canReach_x(C, x, cy, y)) return false;
  }

  if(cy == y){
    if(!canReach_y(C, y, cx, x)) return false;
  }

  if(field[cy][cx] != '.' && field[cy][cx] != C) return false;

  if(cx < x){
    for(int j = x-1 ; j > cx ; j--){
      if(field[y][j] != '.') return false;
    }
  }

  if(cx > x){
    for(int j = x+1 ; j < cx ; j++){
      if(field[y][j] != '.') return false;
    }
  }

  if(cy < y){
    for(int i = y-1 ; i > cy ; i--){
      if(field[i][x] != '.') return false;
    }

  }
  if(cy > y){
    for(int i = y+1 ; i < y ; i++){
      if(field[i][x] != '.') return false;
    }

  }

  return true;
}

