int rec(int x, int y){
  if( x<0 || x>=m ) return 0;
  if( y<0 || y>=n ) return 0;

  char U='^',D='~', R='>', L='<';
  char *u, *d, *r, *l;
  u=&U; d=&D; r=&R; l=&L;
  for( int i=y;i>=0;i-- ) if( c[x][i]!='.' ){ u=&c[x][i]; break; }
  for( int i=y;i<n;i++ )  if( c[x][i]!='.' ){ d=&c[x][i]; break; }
  for( int i=x;i>=0;i-- ) if( c[i][y]!='.' ){ l=&c[i][y]; break; }
  for( int i=x;i<m;i++ )  if( c[i][y]!='.' ){ r=&c[i][y]; break; }

  int ret=0;
  if( *u==*d ){
    *u=*d='.'; ret+=2;
  }else if( *u==*r ){
    *u=*r='.'; ret+=2;
  }else if( *u==*l ){
    *u=*l='.'; ret+=2;
  }
  if( *d==*r && *d!='.' ){
    *d=*r='.'; ret+=2;
  }else if( *d==*l && *d!='.' ){
    *d=*l='.'; ret+=2;
  }

  if( *r==*l && *r!='.' ){
    *r=*l='.'; ret+=2;
  }

  return ret;
}

