using namespace std;

int count(const vector<string>& grid,int x1,int y1,int x2,int y2)
{
	assert(x1==x2 || y1==y2);
	int res=0;
	if(x1==x2){
		if(y1>y2) swap(y1,y2);
		for(int y=y1;y<=y2;y++)
			res+=grid[y][x1]!='.';
	}
	else{
		if(x1>x2) swap(x1,x2);
		for(int x=x1;x<=x2;x++)
			res+=grid[y1][x]!='.';
	}
	return res;
}

bool canerase(const vector<string>& grid,const vector<int>& x,const vector<int>& y)
{
	int x1=x[0],x2=x[1],y1=y[0],y2=y[1];
	if(x1==x2 || y1==y2){
		return abs(x2-x1)+abs(y2-y1)>=2 && count(grid,x1,y1,x2,y2)==2;
	}
	else{
		return count(grid,x1,y1,x2,y1)+count(grid,x2,y1,x2,y2)==2
			|| count(grid,x1,y1,x1,y2)+count(grid,x1,y2,x2,y2)==2;
	}
}

int main()
{
	for(int h,w;cin>>h>>w && h|w;){
		vector<string> grid(h);
		for(int i=0;i<h;i++) cin>>grid[i];

		vector<vector<int>> xs(26),ys(26);
		for(int i=0;i<h;i++)
			for(int j=0;j<w;j++)
				if(grid[i][j]!='.'){
					int k=grid[i][j]-'A';
					xs[k].push_back(j);
					ys[k].push_back(i);
				}

		int res=0;
		for(;;){
			bool flg=false;
			for(int i=0;i<26;i++){
				vector<int>& x=xs[i],y=ys[i];
				if(x.size() && canerase(grid,x,y)){
					grid[y[0]][x[0]]=grid[y[1]][x[1]]='.';
					x.clear(),y.clear();
					res+=2;
					flg=true;
				}
			}
			if(!flg) break;
		}

		cout<<res<<endl;
	}
}

