#define rrep(i,a,b) for(int i=a;i>=b;i--)

int H, W;
int C[500][500];

int count()
{
	rep(y, 0, H) rep(x, 0, W)
	{
		if (C[y][x] != '.') continue;

		int c[26];
		rep(i, 0, 26) c[i] = 0;

		if (y != H - 1) rep(yy, y + 1, H) if (C[yy][x] != '.') 
		{
			c[C[yy][x] - 'A']++;
			break;
		}
		if (y != 0) rrep(yy, y - 1, 0) if (C[yy][x] != '.')
		{
			c[C[yy][x] - 'A']++;
			break;
		}
		if (x != W - 1) rep(xx, x + 1, W) if (C[y][xx] != '.')
		{
			c[C[y][xx] - 'A']++;
			break;
		}
		if (x != 0) rrep(xx, x - 1, 0) if (C[y][xx] != '.')
		{
			c[C[y][xx] - 'A']++;
			break;
		}

		int ret = 0;
		rep(i, 0, 26) if (c[i] == 2) ret += 2;
		if (ret > 0)
		{
			rep(yy, 0, H) rep(xx, 0, W)
			{
				int cc = C[yy][xx] - 'A';
				if (c[cc] == 2) C[yy][xx] = '.';
			}

			return ret;
		}
	}

	return 0;
}

