constexpr int64 MOD = 1000000007;

int M, N;
string c[500];

int main() {
    cin >> M >> N;
    for (int j = 0; j < M; ++j)  {
        cin >> c[j];
    }

    int idx[26], px[26][2], py[26][2];
    fill(idx, idx+26, 0);
    for (int j = 0; j < M; ++j) for (int k = 0; k < N; ++k) {
        if (c[j][k] != '.') {
            int a = c[j][k] - 'A';
            px[a][idx[a]] = j;
            py[a][idx[a]] = k;
            ++idx[a];
        }
    }

    int score = 0;
    bool updated = true;
    while (updated) {
        updated = false;
        for (int a = 0; a < 26; ++a) {
            if (idx[a] == 0) continue;
            int x1 = px[a][0], x2 = px[a][1], y1 = py[a][0], y2 = py[a][1];
            bool ok = true;
            if (x1 == x2) {
                ok = false;
                for (int j = min(y1, y2)+1; j < max(y1, y2); ++j) {
                    ok = true;
                    if (c[x1][j] != '.') { ok = false; break; }
                }
            } else if (y1 == y2) {
                ok = false;
                for (int j = min(x1, x2)+1; j < max(x1, x2); ++j) {
                    ok = true; if (c[j][y1] != '.') { ok = false; break; }
                }
            } else {
                int xl = min(x1, x2), yl = min(y1, y2), xh = max(x1, x2), yh = max(y1, y2); 
                char ch = a+'A';
                bool s1 = true, s2 = true, s3 = true, s4 = true;
                for (int tx = xl; tx <= xh; ++tx) {
                    if (c[tx][yl] != '.' && c[tx][yl] != ch) { s1 = false; break; }
                }
                for (int ty = yl; ty <= yh; ++ty) {
                    if (c[xl][ty] != '.' && c[xl][ty] != ch) { s2 = false; break; }
                }
                for (int tx = xl; tx <= xh; ++tx) {
                    if (c[tx][yh] != '.' && c[tx][yh] != ch) { s3 = false; break; }
                }
                for (int ty = yl; ty <= yh; ++ty) {
                    if (c[xh][ty] != '.' && c[xh][ty] != ch) { s4 = false; break; }
                }
                if ((x2-x1)*(y2-y1) > 0) {
                    ok = (s1 && s4) || (s2 && s3);
                } else {
                    ok = (s1 && s2) || (s3 && s4);
                }
            }
            if (ok) {
                score += 2;
                idx[a] = 0;
                c[x1][y1] = c[x2][y2] = '.';
                updated = true;
                continue;
            }
        }
    }
    cout << score << endl;

    return 0;
}

#include <iostream>
