#define REP(i, n) for (int i = 0; i < (int)(n); i++)
#define FOR(i, s, n) for (int i = (s); i < (int)(n); i++)
#define FOREQ(i, s, n) for (int i = (s); i <= (int)(n); i++)
#define FORIT(it, c) for (__typeof((c).begin())it = (c).begin(); it != (c).end(); it++)
#define MEMSET(v, h) memset((v), h, sizeof(v))


int h, w;
char field[1000][1000];
vector<int> px[30];
vector<int> py[30];
vector<pair<int, int> > cand[30];

void PrintField() {
  REP(y, h) {
    printf("%s\n", field[y]);
  }
}

bool DontHave(int sx, int sy, int ex, int ey, int c) {
  if (sx == ex && sy == ey) { return false; }
  if (sx > ex && sy == ey) { swap(sx, ex); }
  if (sx == ex && sy > ey) { swap(sy, ey); }
  int dx = 0;
  int dy = 0;
  if (sx != ex) { dx++; }
  else { dy++; }
  if (field[sy][sx] == c) { sx += dx; sy += dy; }
  while (sx != ex || sy != ey) {
    if (field[sy][sx] != '.') { return false; }
    sx += dx;
    sy += dy;
  }
  return true;
}

int main() {
  while (scanf("%d %d", &h, &w) > 0) {
    REP(i, 30) {
      cand[i].clear();
      px[i].clear();
      py[i].clear();
    }
    REP(y, h) {
      scanf("%s", field[y]);
    }
    REP(y, h) {
      REP(x, w) {
        if (isupper(field[y][x])) {
          int c = field[y][x] - 'A';
          px[c].push_back(x);
          py[c].push_back(y);
        }
      }
    }
    REP(i, 30) {
      if (px[i].size() == 0) { continue; }
      if (px[i][0] == px[i][1]) {
        int sy = min(py[i][0], py[i][1]) + 1;
        cand[i].push_back(make_pair(px[i][0], sy));
      } else if (py[i][0] == py[i][1]) {
        int sx = min(px[i][0], px[i][1]) + 1;
        cand[i].push_back(make_pair(sx, py[i][0]));
      } else {
        cand[i].push_back(make_pair(px[i][0], py[i][1]));
        cand[i].push_back(make_pair(px[i][1], py[i][0]));
      }
    }

    int ans = 0;
    while (true) {
      bool end = true;
      REP(i, 30) {
        if (px[i].size() == 0) { continue; }
        FORIT(it, cand[i]) {
          if (field[it->second][it->first] != '.') { continue; }
          if (DontHave(px[i][0], py[i][0], it->first, it->second, i + 'A') &&
              DontHave(px[i][1], py[i][1], it->first, it->second, i + 'A')) {
            field[py[i][0]][px[i][0]] = '.';
            field[py[i][1]][px[i][1]] = '.';
            px[i].clear();
            py[i].clear();
            cand[i].clear();
            ans++;
            end = false;
            break;
          }
        }
      }
      if (end) { break; }
    }
    //PrintField();
    printf("%d\n", ans * 2);
  }
}

