	if (ps[k].first < 0) ps[k] = pii(x, y);
	else {
	  int x0 = ps[k].first, y0 = ps[k].second;
	  if (x != x0 && y != y0) {
	    if (flds[y][x0] < 0 || flds[y0][x] < 0)
	      msks[k].push_back(0);
	    else {
	      msks[k].push_back(1 << flds[y][x0]);
	      msks[k].push_back(1 << flds[y0][x]);
	    }
	  }
	  else if (x == x0) {
	    if (y0 + 1 < y) {
	      int msk0 = 0;
	      for (int y1 = y0 + 1; y1 < y; y1++)
		if (flds[y1][x] >= 0) msk0 |= (1 << flds[y1][x]);
