//container util
//------------------------------------------
#define ALL(a)  (a).begin(),(a).end()
#define RALL(a) (a).rbegin(), (a).rend()
#define PB push_back
#define MP make_pair
#define SZ(a) int((a).size())
#define EACH(i,c) for(typeof((c).begin()) i=(c).begin(); i!=(c).end(); ++i)
#define EXIST(s,e) ((s).find(e)!=(s).end())
#define SORT(c) sort((c).begin(),(c).end())

//repetition
//------------------------------------------
#define FOR(i,a,b) for(int i=(a);i<(b);++i)
#define REP(i,n)  FOR(i,0,n)

//constant
//--------------------------------------------
const double EPS = 1e-10;
const double PI  = acos(-1.0);

int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  int H, W; cin >> H >> W;
  VS vs(H);
  REP(i,H) cin >> vs[i];

  vector<PII> memo(32, MP(-1,-1));
  vector<pair<PII,PII>> pos;
  REP(y,H) REP(x,W){
	if(vs[y][x] != '.'){
	  int i = vs[y][x] - 'A';
	  if(memo[i].first < 0)
		memo[i] = MP(x,y);
	  else
		pos.PB(MP(memo[i], MP(x,y)));
	}
  }

  int ans = 0;
  bool up  = true;
  while(up){
	up = false;

	for(int i=0;i<SZ(pos);++i){
	  PII p1 = pos[i].first, p2 = pos[i].second;
	  if(p1 > p2) swap(p1,p2);
	  if((p1.first+1 == p2.first && p1.second == p2.second)
		 || (p1.first == p2.first && abs(p1.second-p2.second)==1)) continue;

	  int ok = 0;
	  for(int j=0;j<SZ(pos);++j){
		if(i == j) continue;
		PII q1 = pos[j].first, q2 = pos[j].second;
		if((q1.second == p1.second && p1.first <= q1.first && q1.first <= p2.first)
		   || ((q2.second == p1.second) && p1.first <= q2.first && q2.first <= p2.first)){
		  ok |= 1;
		}
		if((q1.second == p2.second && p1.first <= q1.first && q1.first <= p2.first)
		   || ((q2.second == p2.second) && p1.first <= q2.first && q2.first <= p2.first)){
		  ok |= 2;
		}

		if(p1.first == p2.first){
		  if((q1.first == p1.first && p1.second <= q1.second && q1.second <= p2.second)
			 || (q2.first == p1.first && p1.second <= q2.second && q2.second <= p2.second))
			ok = 3;
		}
	  }
	  if(ok != 3){
		pos.erase(pos.begin() + i);
		ans += 2;
		up = true;
		break;
	  }
	}
  }

  cout << ans << endl;

  return 0;
}

