	  int nx = j+dx[k], ny = i+dy[k];
	  if(nx < 0 || nx > W || ny < 0 || ny > H) continue;
	  if(field[i][j] == field[ny][nx]) found[field[i][j]-'A'] = false;
	}
      }
    }

    int ans = 0;
    for(; ;){
    Start:;
      bool flag = false;
      rep(i, 26){
	if(found[i]){	  	 
	  int X1 = cross[i].x1, Y1 = cross[i].y1, X2 = cross[i].x2, Y2 = cross[i].y2;
	  int x1 = alpha[i].x1, y1 = alpha[i].y1, x2 = alpha[i].x2, y2 = alpha[i].y2;
	  char c = 'A'+i;

	  if(x1 == x2){
	    flag = checkW(y1, y2, x1, c);
	  }
	  else if(y1 == y2){
	    flag = checkH(x1, x2, y1, c);	    
	  }
	  else{
	    if(((checkH(X1, x2, Y1, c) && (checkW(Y1, y1, X1, c)))) ||
	       ((checkH(X2, x1, Y2, c) && (checkW(Y2, y2, X2, c))))){
	      flag = true;
	    }
	  }

	  if(flag){
	    //cout << c << endl;
