                if(valid(cx,cy) && g[cy][cx]!='.'){
                    s[g[cy][cx]].emplace_back(cx,cy);
                }
            }
            for(auto & ps : s){
                if(ps.size()>=2){
                    for(auto & p : ps){
                        int x,y;
                        tie(x,y) = p;
                        g[y][x] = '.';
                        ans++;
                        finish = false;
                        space.emplace_back(x,y);
                    }
                }
            }
        }
        if(finish) break;
    }
    return ans;
}

