#include<bits/stdc++.h>
using namespace std;
typedef pair<int,int> P;
#define MAX 1000
char t[MAX][MAX];
bool f[26];
int h,w;
vector<int> Y[26],X[26];

bool calc(int ay,int ax,int by,int bx){
  if(ay>by)swap(ay,by);
  if(ax>bx)swap(ax,bx);
  for(int i=ay;i<=by;i++)
    for(int j=ax;j<=bx;j++)
      if(t[i][j]!='.')return false;
  return true;
}

bool check(int id){
  int ay=Y[id][0];
  int ax=X[id][0];
  int by=Y[id][1];
  int bx=X[id][1];

  if(abs(ay-by)+abs(ax-bx)==1)return false;

  t[ay][ax]='.';
  t[by][bx]='.';

  if(calc(ay,ax,ay,bx)&&
     calc(ay,bx,by,bx)&&
     t[ay][bx]=='.'){
    f[id]=false;
    return true;
  }

  if(calc(ay,ax,by,ax)&&
     calc(by,ax,by,bx)&&
     t[by][ax]=='.'){
    f[id]=false;
    return true;
  }

  t[ay][ax]='A'+id;
  t[by][bx]='A'+id;
  return false;
}

int main(){
  cin>>h>>w;
  for(int i=0;i<h;i++){
    for(int j=0;j<w;j++){
      cin>>t[i][j];
      if(t[i][j]=='.')continue;
      int id=t[i][j]-'A';
      Y[id].push_back(i);
      X[id].push_back(j);
      f[id]=true;
    }
  }

  int ans=0,cnt;
  while(1){
    cnt=0;
    for(int i=0;i<26;i++){
      if(!f[i])continue;
      if(check(i))cnt+=2;
    }
    if(cnt==0)break;
    ans+=cnt;
  }
  cout<<ans<<endl;
  return 0;
}

