	if(m.count(a))return m[a];
	int ret=__builtin_popcount(a);
	for(int i=0;i<26;i++)if(a&(1<<i)){
		for(int j=0;j<w[i].size();j++){
			if(!(a&w[i][j])){
				ret=min(ret,solve(a-(1<<i)));
			}
		}
	}
	return m[a]=ret;
}
