				if(c[i][1]==c[j][1]&&r[i][0]<=r[j][1]&&r[j][1]<=r[i][1])key|=(1<<j);
			}
			w[i].push_back(key);
			key=0;
			for(int j=0;j<26;j++){
				if(i==j)continue;
				if(r[i][1]==r[j][0]&&min(c[i][0],c[i][1])<=c[j][0]&&c[j][0]<=max(c[i][0],c[i][1]))key|=(1<<j);
				if(c[i][0]==c[j][0]&&r[i][0]<=r[j][0]&&r[j][0]<=r[i][1])key|=(1<<j);
				if(r[i][1]==r[j][1]&&min(c[i][0],c[i][1])<=c[j][1]&&c[j][1]<=max(c[i][0],c[i][1]))key|=(1<<j);
				if(c[i][0]==c[j][1]&&r[i][0]<=r[j][1]&&r[j][1]<=r[i][1])key|=(1<<j);
			}
			w[i].push_back(key);
