                    for(int k=j+1;k<m;k++){
                        if(f[i][k]!='.'){
                            cnt[f[i][k]]++;
                            break;
                        }
                    }
                    for(int k=j-1;k>=0;k--){
                        if(f[i][k]!='.'){
                            cnt[f[i][k]]++;
                            break;
                        }
                    }
                    for(auto &e:cnt){
                        if(e.second==2){
                            update=true;
                            res+=2;
                            for(int k=0;k<n;k++){
                                if(f[k][j]==e.first) f[k][j]='.';
                            }
                            for(int k=0;k<m;k++){
                                if(f[i][k]==e.first) f[i][k]='.';
                            }
                        }
                    }
                }
            }
        }
    }
    cout<<res<<endl;
    return 0;
}

