int dw[] = {0, 0, 1, -1};

inline bool in_field(int h, int w){
    return 0<=h&&h<M && 0<=w&&w<N;
}

void solve(){
    bool updated = true;
    int count = 0;
    while (updated) {
        updated = false;
        for(int i=0; i<M; i++){
            for(int j=0; j<N; j++) if(field[i][j] == '.'){
                vector<tuple<char, int, int>> colors;

                for(int d=0; d<4; d++){
                    int l = 0;
                    while(in_field(i+dh[d]*l, j+dw[d]*l) && field[i+dh[d]*l][j+dw[d]*l] == '.') l++;

                    if(!in_field(i+dh[d]*l, j+dw[d]*l)) continue;

                    colors.push_back({field[i+dh[d]*l][j+dw[d]*l], i+dh[d]*l, j+dw[d]*l});
                }

                for(int i=0; i<colors.size(); i++){
                    for(int j=i+1; j<colors.size(); j++){
                        char c1, c2;
                        int h1, w1, h2, w2;
                        tie(c1, h1, w1) = colors[i];
                        tie(c2, h2, w2) = colors[j];
                        if(c1 == c2){
                            field[h1][w1] = field[h2][w2] = '.';
                            count += 2;
                            updated = true;
                        }
                    }
                }
            }
        }
    }
    cout << count << endl;
}

