int main()
{
	int n, m;
	cin >> n >> m;
	vs fld(n);
	REP(i, n) cin >> fld[i];
	vector<vector<pii>> v(26);
	REP(i, n)REP(j, m)
	{
		if (fld[i][j] != '.')
		{
			v[fld[i][j] - 'A'].emplace_back(i, j);
		}
	}
	bool update = true;
	int ans = 0;
	while (update)
	{
		update = false;
		REP(i, 26)
		{
			if (v[i].size() == 0) continue;
			int ax = v[i][0].first, ay = v[i][0].second;
			int bx = v[i][1].first, by = v[i][1].second;
			if (ax == bx)
			{
				bool f = true;
				if (ay > by) swap(ay, by);
				FOR(j, ay + 1, by)
				{
					if (fld[ax][j] != '.') f = false;
				}
				if (f&&abs(ay-by)>1)
				{
					update = true;
					v[i].clear();
					ans++;
					fld[ax][ay] = fld[bx][by] = '.';
				}
			}
			else if (ay == by)
			{
				bool f = true;
				if (ax > bx) swap(ax, bx);
				FOR(j, ay + 1, by)
				{
					if (fld[j][ay] != '.') f = false;
				}
				if (f&&abs(ax - bx)>1)
				{
					update = true;
					v[i].clear();
					ans++;
					fld[ax][ay] = fld[bx][by] = '.';
				}
			}
			else
			{
				if (ay > by)
				{
					swap(ax, bx), swap(ay, by);
				}
				if (ax > bx)
				{
					bool f1 = true;
					FOR(j, ay + 1, by)
					{
						if (fld[ax][j] != '.') f1 = false;
					}
					FOR(j, bx + 1, ax + 1)
					{
						if (fld[j][by] != '.') f1 = false;
					}
					bool f2 = true;
					FOR(j, bx, ax)
					{
						if (fld[j][ay] != '.') f2 = false;
					}
					FOR(j, ay, by)
					{
						if (fld[bx][j] != '.') f2 = false;
					}
					if (f1 || f2)
					{
						update = true;
						v[i].clear();
						ans++;
						fld[ax][ay] = fld[bx][by] = '.';
					}
				}
				if (ax < bx)
				{
					bool f1 = true;
					FOR(j, ay + 1, by+1)
					{
						if (fld[ax][j] != '.') f1 = false;
					}
					FOR(j, ax, bx)
					{
						if (fld[j][by] != '.') f1 = false;
					}
					bool f2 = true;
					FOR(j, ax+1, bx)
					{
						if (fld[j][ay] != '.') f2 = false;
					}
					FOR(j, ay, by)
					{
						if (fld[bx][j] != '.') f2 = false;
					}
					if (f1 || f2)
					{
						update = true;
						v[i].clear();
						ans++;
						fld[ax][ay] = fld[bx][by] = '.';
					}
				}
			}
		}
	}
	cout << ans * 2 << endl;
	return 0;
}

