const int NONE = 0;
const int VERTICAL = 1;
const int HORIZONTAL = 2;
const int CORNER = 3;

int main() {
	int M, N;
	cin >> M >> N;

	vs field(M);
	vvi pos(26, vi(4, -1));
	REP(i, M) {
		cin >> field[i];
		REP(j, N) {
			if(field[i][j] != '.') {
				int k = field[i][j]-'A';
				if(pos[k][0] == -1) {
					pos[k][0] = i;
					pos[k][1] = j;
				} else {
					pos[k][2] = i;
					pos[k][3] = j;
				}
			}
		}
	}

	vi type(26, CORNER);
	REP(i, 26) {
		if(pos[i][2] == -1) {
			type[i] = NONE;
			continue;
		}
		if(abs(pos[i][0]-pos[i][2]+pos[i][1]-pos[i][3]) == 1) {
			type[i] = NONE;
			continue;
		}
		if(pos[i][0] == pos[i][2]) {
			type[i] = VERTICAL;
			continue;
		}
		if(pos[i][1] == pos[i][3]) {
			type[i] = HORIZONTAL;
			continue;
		}
	}

	int ans = 0;
	bool updated = true;
	while(updated) {
		updated = false;
		REP(i, 26) {
			if(type[i] == NONE) {

			} else if(type[i] == VERTICAL) {
				int y = pos[i][0];
				FOR(x, min(pos[i][1], pos[i][3])+1, max(pos[i][1], pos[i][3])-1) {
					if(field[y][x] != '.') {
						goto next;
					}
				}
				ans += 2;
				type[i] = NONE;
				field[pos[i][0]][pos[i][1]] = field[pos[i][2]][pos[i][3]] = '.';
				updated = true;
			} else if(type[i] ==  HORIZONTAL) {
				int x = pos[i][1];
				FOR(y, min(pos[i][0], pos[i][2])+1, max(pos[i][0], pos[i][2])-1) {
					if(field[y][x] != '.') {
						goto next;
					}
				}
				ans += 2;
				type[i] = NONE;
				field[pos[i][0]][pos[i][1]] = field[pos[i][2]][pos[i][3]] = '.';
				updated = true;
			} else if(type[i] == CORNER) {
				if(field[pos[i][0]][pos[i][3]] == '.' ||  field[pos[i][2]][pos[i][1]] == '.') {
					ans += 2;
					type[i] = NONE;
					field[pos[i][0]][pos[i][1]] = field[pos[i][2]][pos[i][3]] = '.';
					updated = true;
				}
			}
			next:;
		}
	}

	cout << ans << endl;
	return 0;
}

