int main(){
  int N, M;
  cin >> N >> M;
  vector<string> field(N);
  REP(i, N) cin >> field[i];

  vector<int> rows;
  vector<int> cols;

  REP(i, N)REP(j, M){
    if(isalpha(field[i][j])){
      rows.push_back(i);
      cols.push_back(j);
    }
  }

  sort(ALL(rows));
  sort(ALL(cols));
  rows.erase(unique(ALL(rows)), rows.end());
  cols.erase(unique(ALL(cols)), cols.end());

  vector<string> board(rows.size(), string(cols.size(), '.'));
  REP(i, N)REP(j, M){
    if(isalpha(field[i][j])){
      int r = lower_bound(ALL(rows), i) - rows.begin();
      int c = lower_bound(ALL(cols), j) - cols.begin();
      board[r][c] = field[i][j];
    }
  }
  N = rows.size();
  M = cols.size();

  int res = 0;
  int dx[4] = {0, -1, 0, 1};
  int dy[4] = {1, 0, -1, 0};
  bool update = true;
  while(update){
    update = false;

    REP(i, N)REP(j, M){
      if(board[i][j] == '.'){
        vector<int> t_row;
        vector<int> t_col;

        REP(k, 4){
          int i2 = i + dx[k];
          int j2 = j + dy[k];
          while(0 <= i2 && i2 < N && 0 <= j2 && j2 < M && board[i2][j2] == '.'){
            i2 += dx[k];
            j2 += dy[k];
          }
          if(0 <= i2 && i2 < N && 0 <= j2 && j2 < M){
            t_row.push_back(i2);
            t_col.push_back(j2);
          }
        }

        vector<int> c(26, 0);
        REP(k, 26){
          int c = 0;
          REP(l, t_row.size()){
            if(board[t_row[l]][t_col[l]] == 'A' + k) c++;
          }
          if(c == 2){
            res += 2;
            update = true;
            REP(l, t_row.size()){
              if(board[t_row[l]][t_col[l]] == 'A' + k){
                board[t_row[l]][t_col[l]] = '.';
              }
            }
          }
        }
      }
    }
  }

  cout << res << endl;
  return 0;
}

#include <iostream>
