#define REP(i, a, n) for(int i = ((int) a); i < ((int) n); i++)
using namespace std;

int M, N, K;
char S[550][550];
vector<int> y, x;
vector<char> c;
char s[550][550];
int dd[5] = { 0, 1, 0, -1, 0 };

int compress(vector<int> &a) {
  vector<int> v;

  REP(i, 0, a.size()) v.push_back(a[i]);
  sort(v.begin(), v.end());
  v.erase(unique(v.begin(), v.end()), v.end());
  REP(i, 0, a.size()) a[i] = find(v.begin(), v.end(), a[i]) - v.begin();

  return (int) v.size();
}

bool isin(int y, int x) {
  return 0 <= y && y < M && 0 <= x && x < N;
}

int main(void) {
  cin >> M >> N;
  K = 0;
  REP(i, 0, M) REP(j, 0, N) {
    cin >> S[i][j];
    if(S[i][j] != '.') {
      y.push_back(i);
      x.push_back(j);
      c.push_back(S[i][j]);
      K++;
    }
  }
  REP(i, 0, M) REP(j, 0, N) REP(k, 0, 4) if(isin(y[i] + dd[i], x[k] + dd[k + 1])) {
    y.push_back(y[i] + dd[k]);
    x.push_back(x[i] + dd[k + 1]);
  }

  M = compress(y);
  N = compress(x);

  REP(i, 0, M) REP(j, 0, N) s[i][j] = '.';
  REP(i, 0, K) s[y[i]][x[i]] = c[i];

  int ans = 0;
  bool visited[256];
  REP(i, 0, 256) visited[i] = false;
  bool f = true;
  while(f) {
    f = false;
    REP(i, 0, M) REP(j, 0, N) {
      if(s[i][j] == '.' || visited[s[i][j]]) {
        int cnt[256];
        REP(k, 0, 256) cnt[k] = 0;
        REP(k, 0, 4) {
          int ny = i + dd[k], nx = j + dd[k + 1];
          while(isin(ny, nx)) {
            if(s[ny][nx] != '.' && !visited[s[ny][nx]]) {
              cnt[s[ny][nx]]++;
              if(cnt[s[ny][nx]] == 2) {
                visited[s[ny][nx]] = true;
                ans += 2;
                f = true;
              }
              break;
            }
            ny += dd[k];
            nx += dd[k + 1];
          }
        }
      }
    }
  }
  cout << ans << endl;

  return 0;
}

