int main() {
  size_t M, N;
  scanf("%zu %zu", &M, &N);

  std::vector<std::string> C(M);
  std::vector<std::vector<std::pair<size_t, size_t>>> pos(26);
  int alph = 1 << 27;
  for (size_t i = 0; i < M; ++i) {
    char buf[512];
    scanf("%s", buf);
    C[i] = buf;
    for (size_t j = 0; j < N; ++j) {
      if (C[i][j] != '.') {
        int a = C[i][j] - 'A';
        pos[a].emplace_back(i, j);
        alph |= 1 << a;
      }
    }
  }

  std::vector<std::vector<int>> flags(26);
  for (size_t k = 0; k < 26; ++k) {
    if (pos[k].empty()) {
      flags[k] = {1 << 27};
      continue;
    }
    size_t imax, imin, jmax, jmin;
    std::tie(imin, imax) = std::minmax(pos[k][0].first, pos[k][1].first);
    std::tie(jmin, jmax) = std::minmax(pos[k][0].second, pos[k][1].second);
    size_t di = imax - imin;
    size_t dj = jmax - jmin;

    if (di + dj == 1) {
      flags[k] = {1 << 27};
      continue;
    }

    if (imax == imin) {
      int f = 0;
      for (size_t j = jmin+1; j < jmax; ++j) {
        if (C[imin][j] != '.')
          f |= 1 << (C[imin][j] - 'A');
      }
      flags[k].push_back(f);
    } else if (jmax == jmin) {
      int f = 0;
      for (size_t i = imin+1; i < imax; ++i) {
        if (C[i][jmin] != '.')
          f |= 1 << (C[i][jmin] - 'A');
      }
      flags[k].push_back(f);
    } else {
      int f0 = 0;
      int f1 = 0;
      int f2 = 0;
      int f3 = 0;

      for (size_t j = jmin+1; j < jmax; ++j) {
        if (C[imin][j] != '.') f0 |= 1 << (C[imin][j] - 'A');
        if (C[imax][j] != '.') f1 |= 1 << (C[imax][j] - 'A');
      }
      for (size_t i = imin+1; i < imax; ++i) {
        if (C[i][jmin] != '.') f2 |= 1 << (C[i][jmin] - 'A');
        if (C[i][jmax] != '.') f3 |= 1 << (C[i][jmax] - 'A');
      }

      if (pos[k][0].second < pos[k][1].second) {
        flags[k] = {f0|f3, f1|f2};
        if (C[imin][jmax] != '.') flags[k][0] |= 1 << (C[imin][jmax] - 'A');
        if (C[imax][jmin] != '.') flags[k][1] |= 1 << (C[imax][jmin] - 'A');
      } else {
        flags[k] = {f0|f2, f1|f3};
        if (C[imin][jmin] != '.') flags[k][0] |= 1 << (C[imin][jmin] - 'A');
        if (C[imax][jmax] != '.') flags[k][1] |= 1 << (C[imax][jmax] - 'A');
      }
    }
  }

  // for (int i = 0; i < 26; ++i) {
  //   for (auto& fs: flags[i]) {
  //     fprintf(stderr, "%c:", 'A'+i);
  //     for (int j = 0; j < 26; ++j) {
  //       if (fs >> j & 1)
  //         fprintf(stderr, " %c", 'A'+j);
  //     }
  //     fprintf(stderr, "\n");
  //   }
  // }

  int res = 0;
  for (int i = 0; i < 26; ++i) {
    for (int j = 0; j < 26; ++j) {
      if (!(alph >> j & 1)) continue;
      for (int rm: flags[j]) {
        if (rm & alph) continue;
        alph ^= 1 << j;
        // fprintf(stderr, "Removed %c\n", 'A'+j);
        res += 2;
        flags[j].clear();
        break;
      }
    }
  }

  printf("%d\n", res);
}

