                    if (C[si][j] != '.') valid = false;
                }
            }
            else if (sj == tj) {
                if (abs(si - ti) == 1) valid = false;
                FOR(i, min(si, ti)+1, max(si, ti)) {
                    if (C[i][sj] != '.') valid = false;
                }
            }
            else {
                valid = false;
                {
                    bool v = true;
                    if (C[si][tj] != '.') v = false;
                    FOR(i, min(si, ti) + 1, max(si, ti)) {
                        if (C[i][tj] != '.') v = false;
                    }
                    FOR(j, min(sj, tj) + 1, max(sj, tj)) {
                        if (C[si][j] != '.') v = false;
                    }
                    if (v) valid = true;
                }
                {
                    bool v = true;
                    if (C[ti][sj] != '.') v = false;
                    FOR(i, min(si, ti) + 1, max(si, ti)) {
                        if (C[i][sj] != '.') v = false;
                    }
                    FOR(j, min(sj, tj) + 1, max(sj, tj)) {
                        if (C[ti][j] != '.') v = false;
                    }
                    if (v) valid = true;
                }
            }
            if (valid) {
                C[si][sj] = C[ti][tj] = '.';
                updated = true;
                ans += 2;
                break;
            }
        }
    }

    cout << ans << endl;

    return 0;
}

