    for (int j = 1; j <= N; j++){
      cin >> C[i][j];
    }
  }
  vector<vector<pair<int, int>>> P(26);
  for (int i = 1; i <= M; i++){
    for (int j = 1; j <= N; j++){
      if (C[i][j] != '.'){
        P[C[i][j] - 'A'].push_back(make_pair(i, j));
      }
    }
  }
  vector<pair<int, int>> S;
  for (int i = 0; i < 26; i++){
    if (!P[i].empty()){
      int y1 = P[i][0].first;
      int x1 = P[i][0].second;
      int y2 = P[i][1].first;
      int x2 = P[i][1].second;
      if (y1 == y2){
        if (abs(x2 - x1) >= 2){
          S.push_back(make_pair(y1, (x1 + x2) / 2));
        }
      } else if (x1 == x2){
        if (abs(y2 - y1) >= 2){
          S.push_back(make_pair((y1 + y2) / 2, x1));
        }
      } else {
        S.push_back(make_pair(y1, x2));
        S.push_back(make_pair(y2, x1));
      }
    }
  }
  vector<bool> c(26, true);
  for (int i = 0; i < 26; i++){
    if (P[i].empty()){
      c[i] = false;
    }
  }
  int ans = 0;
  for (int i = 0; i < 26; i++){
    int cnt = S.size();
    for (int j = 0; j < cnt; j++){
      if (C[S[j].first][S[j].second] == '.'){
        vector<int> cnt2(26, 0);
        for (int k = 0; k < 4; k++){
          int y = S[j].first;
          int x = S[j].second;
          while (C[y][x] == '.'){
            y += dy[k];
            x += dx[k];
          }
          if (C[y][x] != '#'){
            cnt2[C[y][x] - 'A']++;
          }
        }
        for (int k = 0; k < 26; k++){
          if (cnt2[k] == 2){
            c[k] = false;
            ans += 2;
            for (int l = 0; l < 2; l++){
              C[P[k][l].first][P[k][l].second] = '.';
            }
          }
        }
      }
    }
  }
  cout << ans << endl;
}

