	while (1) {
		bool updated = false;
		REP(i, 26) {
			if (b[i] || pos[i].empty())continue;
			int x1 = pos[i][0].first, y1 = pos[i][0].second,
				x2 = pos[i][1].first, y2 = pos[i][1].second;
			bool ok = true;
			if (x1 == x2) {
				if (abs(y1 - y2) == 1)continue;

				FOR(i, min(y1, y2) + 1, max(y1, y2)) {
					if (f(x1, i))ok = false;
				}
			}
			else if (y1 == y2) {
				if (abs(x1 - x2) == 1)continue;
				FOR(i, min(x1, x2) + 1, max(x1, x2)) {
					if (f(i, y1))ok = false;
				}
			}
			else {
				if (x1 > x2) {
					swap(x1, x2);
					swap(y1, y2);
				}
				ok = false;
				bool flag = true;
				FOR(i, x1 + 1, x2) {
					if (f(i, y1))flag = false;
				}
				FOR(i, min(y1, y2) + 1, max(y1, y2)) {
					if (f(x2, i))flag = false;
				}
				if (f(x2, y1))flag = false;
				if (flag)ok = true;
				flag = true;
				FOR(i, x1 + 1, x2) {
					if (f(i, y2))flag = false;
				}
				FOR(i, min(y1, y2) + 1, max(y1, y2)) {
					if (f(x1, i))flag = false;
				}
				if (f(x1, y2))flag = false;
				if (flag)ok = true;

			}
			if (ok) {
				updated = true;
				b[i] = 1;
				cnt++;
			}
		}
		if (!updated)break;

	}
	cout << 2 * cnt << endl;
}

