#include <iostream>
#include <vector>
#include <string>
#include <cctype>
#include <cstdlib>

using namespace std;

struct Pos {
    int r, c;

    Pos() {}
    Pos(int r, int c) : r(r), c(c) {}
};

inline int toidx(char c) {
    return c - 'A';
}

inline int sign(int n) {
    if(n == 0) return 0;
    return n < 0 ? -1 : 1;
}

inline int dist(const Pos &p1, const Pos &p2) {
    return abs(p1.r-p2.r) + abs(p1.c-p2.c);
}

int main() {
    ios::sync_with_stdio(0);

    int R, C;
    cin >> R >> C;

    vector<string> v;
    vector<vector<Pos> > pos(26);
    for(int i = 0; i < R; ++i) {
        string line;
        cin >> line;

        for(int j = 0; j < line.size(); ++j) {
            if(isalpha(line[j])) {
                pos[toidx(line[j])].push_back(Pos(i, j));
            }
        }

        v.push_back(line);
    }

    int score = 0;
    vector<int> used(26, 0);
    while(true) {
        bool modified = false;

        for(int i = 0; i < pos.size(); ++i) {
            if(used[i]) continue;
            if(pos[i].size() == 0) continue;

            const Pos &p1 = pos[i][0];
            const Pos &p2 = pos[i][1];
            if(dist(p1, p2) == 1) continue;

            int r1 = p1.r;
            int c1 = p2.c;
            bool ok = true;
            for(int c = c1; c != p1.c; c += sign(p1.c-c)) {
                if(v[r1][c] != '.' && v[r1][c] != i+'A') ok = false;
            }
            for(int r = r1; r != p2.r; r += sign(p2.r-r)) {
                if(v[r][c1] != '.' && v[r][c1] != i+'A') ok = false;
            }
            if(!ok) {
                int r2 = p2.r;
                int c2 = p1.c;
                ok = true;
                for(int c = c2; c != p2.c; c += sign(p2.c-c)) {
                    if(v[r2][c] != '.' && v[r2][c] != i+'A') ok = false;
                }
                for(int r = r2; r != p1.r; r += sign(p1.r-r)) {
                    if(v[r][c2] != '.' && v[r][c2] != i+'A') ok = false;
                }
            }

            if(ok) {
                score += 2;
                v[p1.r][p1.c] = v[p2.r][p2.c] = '.';
                used[i] = true;
                modified = true;
            }
        }

        if(!modified) break;
    }

    cout << score << endl;

    return 0;
}

